$(document).ready(function(){var navBut=document.getElementById("navIcon"),navMobile=document.getElementById("navMobile"),caption=document.getElementById("caption");const mq=window.matchMedia("(max-width: 600px)");$(navBut).click(function(){$(navMobile).fadeToggle(300,function(){$(caption).fadeToggle(300)})}),$(window).resize(function(){mq.matches?caption.style.top=0:($(navMobile).hide(),caption.style.display="flex",caption.style.top="120px")}),setTimeout(function start(){$(".skillBars").each(function(i){var $bar=$(this);$(this).append('<span class="percent"></span>'),setTimeout(function(){$bar.css("width",$bar.attr("data-percent"))},100*i)}),$(".percent").each(function(){$(this).prop("Counter",0).animate({Counter:$(this).parent(".skillBars").attr("data-percent")},{duration:2e3,easing:"swing",step:function(now){$(this).text(Math.ceil(now)+"%")}})})},300)});